"use strict";import BCScan from"./BCScan.min.js";export default class SplashEventHandler{constructor(){this.handleItemEntryBtn(),this.handleSplashScanBtn()}handleItemEntryBtn(){let itemEntryButtons=document.getElementsByName("itemEntryBtn");for(let i=0;i<itemEntryButtons.length;i++)itemEntryButtons[i].addEventListener("click",()=>{document.getElementById("splashDiv").style.display="none",document.getElementById("scanResultsExistsDiv").style.display="none",document.getElementById("scanResultsNotExistDiv").style.display="none",document.getElementById("itemEntryDiv").style.display="block"})}handleSplashScanBtn(){document.getElementById("splashScanBtn").addEventListener("click",()=>{this.BCScan=new BCScan,document.getElementById("splashScanDiv").style.display="none",document.getElementById("scannerDiv").style.display="block"})}};